package com.Operators;

public class BitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=995,b=343;
		System.out.println("Bitwise Operators");
		System.out.println(a&b);
		System.out.println(a|b);
		System.out.println(a^ b);

	}

}
